'use client';

import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Quote, RefreshCw } from 'lucide-react';
import { quotesApi } from '@/lib/api';
import type { Quote as QuoteType } from '@/lib/types';
import { toast } from 'sonner';

export default function DailyQuote() {
    const [quote, setQuote] = useState<QuoteType | null>(null);
    const [loading, setLoading] = useState(true);
    const [refreshing, setRefreshing] = useState(false);

    const fetchQuote = async (random = false) => {
        try {
            const res = random ? await quotesApi.random() : await quotesApi.today();
            setQuote(res.quote);
        } catch {
            toast.error('Failed to load quote.');
        }
    };

    useEffect(() => {
        let isMounted = true;
        fetchQuote().finally(() => {
            if (isMounted) setLoading(false);
        });
        return () => { isMounted = false; };
    }, []);

    const handleRefresh = async () => {
        setRefreshing(true);
        await fetchQuote(true);
        setRefreshing(false);
    };

    return (
        <div
            className="rounded-xl p-4 relative overflow-hidden"
            style={{
                background: 'linear-gradient(135deg, rgba(108,99,255,0.12) 0%, rgba(0,212,170,0.08) 100%)',
                border: '1px solid rgba(108,99,255,0.2)',
            }}
            aria-label="Daily motivational quote"
        >
            {/* Decorative quote mark */}
            <Quote
                className="absolute top-4 right-4 w-16 h-16 opacity-5"
                aria-hidden="true"
            />

            <div className="flex items-start gap-3">
                <div
                    className="w-1 rounded-full flex-shrink-0 mt-1"
                    style={{ height: '3rem', background: 'linear-gradient(180deg, var(--primary), var(--accent))' }}
                    aria-hidden="true"
                />

                <div className="flex-1 min-w-0">
                    {loading ? (
                        <div aria-busy="true" aria-label="Loading quote...">
                            <div className="skeleton h-4 rounded mb-2" style={{ width: '90%' }} />
                            <div className="skeleton h-4 rounded mb-3" style={{ width: '70%' }} />
                            <div className="skeleton h-3 rounded" style={{ width: '30%' }} />
                        </div>
                    ) : (
                        <AnimatePresence mode="wait">
                            {quote && (
                                <motion.div
                                    key={quote.text}
                                    initial={{ opacity: 0, y: 6 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    exit={{ opacity: 0, y: -6 }}
                                    transition={{ duration: 0.35, ease: 'easeOut' }}
                                >
                                    <blockquote aria-live="polite">
                                        <p className="text-sm leading-relaxed font-medium mb-2">
                                            &ldquo;{quote.text}&rdquo;
                                        </p>
                                        <footer>
                                            <cite
                                                className="text-xs font-medium not-italic"
                                                style={{ color: 'var(--accent)' }}
                                            >
                                                ΓÇö {quote.author}
                                            </cite>
                                        </footer>
                                    </blockquote>
                                </motion.div>
                            )}
                        </AnimatePresence>
                    )}
                </div>

                {/* Refresh button */}
                <button
                    onClick={handleRefresh}
                    disabled={refreshing || loading}
                    className="p-1.5 rounded-lg hover:bg-foreground/[0.05] transition-all flex-shrink-0 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-cyan-400 disabled:opacity-40"
                    aria-label="Get a different quote"
                    title="Refresh quote"
                >
                    <RefreshCw
                        className={`w-3.5 h-3.5 ${refreshing ? 'animate-spin' : ''}`}
                        style={{ color: 'var(--muted-foreground)' }}
                        aria-hidden="true"
                    />
                </button>
            </div>
        </div>
    );
}

'use client';

import { useState } from 'react';
import { Bell, Save, Clock, CheckCircle2, User, Sparkles, Volume2, Trash2, AlertTriangle } from 'lucide-react';
import { useAuth } from '@/contexts/auth-context';
import { authApi } from '@/lib/api';
import { toast } from 'sonner';
import { motion, AnimatePresence } from 'framer-motion';

export default function SettingsCard() {
    const { user, setUser, deleteAccount } = useAuth();
    const [name, setName] = useState(user?.name || '');
    const [reminderTime, setReminderTime] = useState(user?.reminderTime || '09:00');
    const [confettiEnabled, setConfettiEnabled] = useState(user?.confettiEnabled ?? true);
    const [soundEnabled, setSoundEnabled] = useState(user?.soundEnabled ?? true);

    const [saving, setSaving] = useState(false);
    const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
    const [deleting, setDeleting] = useState(false);

    const hasChanges =
        name !== user?.name ||
        reminderTime !== user?.reminderTime ||
        confettiEnabled !== user?.confettiEnabled ||
        soundEnabled !== user?.soundEnabled;

    const handleSave = async (e: React.FormEvent) => {
        e.preventDefault();
        setSaving(true);
        try {
            const res = await authApi.updateSettings({
                name,
                reminderTime,
                confettiEnabled,
                soundEnabled
            });
            setUser(res.user);
            toast.success('Settings updated!');
        } catch {
            toast.error('Failed to save settings.');
        } finally {
            setSaving(false);
        }
    };

    const handleDeleteAccount = async () => {
        setDeleting(true);
        try {
            await deleteAccount();
            toast.success('Account deleted. WeΓÇÖre sorry to see you go.');
        } catch {
            toast.error('Failed to delete account.');
            setDeleting(false);
        }
    };

    return (
        <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            className="glass rounded-xl p-4 h-full flex flex-col"
            style={{ border: '1px solid var(--card-border)' }}
        >
            <div className="flex items-center gap-3 mb-4">
                <div className="w-9 h-9 rounded-lg bg-primary/10 flex items-center justify-center text-primary">
                    <User className="w-5 h-5" />
                </div>
                <div>
                    <h2 className="text-xl font-display font-bold text-foreground">Account Settings</h2>
                    <p className="text-xs text-foreground/40">Manage your profile and preferences</p>
                </div>
            </div>

            <form onSubmit={handleSave} className="flex-1 space-y-4">
                {/* Profile Section */}
                <div className="space-y-4">
                    <h3 className="text-[10px] uppercase tracking-widest font-bold text-muted-foreground flex items-center gap-2">
                        <User className="w-3 h-3" /> Personal Information
                    </h3>
                    <div className="space-y-3">
                        <input
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            placeholder="Full Name"
                            className="w-full bg-foreground/[0.03] border border-foreground/[0.05] rounded-xl px-4 py-2 text-sm text-foreground focus:outline-none focus:ring-1 focus:ring-accent transition-all hover:border-foreground/[0.1]"
                        />
                        <input
                            type="email"
                            value={user?.email}
                            disabled
                            className="w-full bg-foreground/[0.03] border border-foreground/[0.05] rounded-xl px-4 py-2 text-sm text-foreground opacity-50 cursor-not-allowed"
                        />
                    </div>
                </div>

                {/* Preferences Section */}
                <div className="space-y-4">
                    <h3 className="text-[10px] uppercase tracking-widest font-bold text-foreground/30 flex items-center gap-2">
                        <Sparkles className="w-3 h-3" /> Experience Defaults
                    </h3>

                    <div className="grid grid-cols-1 gap-2">
                        <div className="flex items-center justify-between p-3 rounded-xl bg-foreground/[0.03] border border-foreground/[0.05]">
                            <div className="flex items-center gap-3">
                                <Sparkles className="w-4 h-4 text-accent" />
                                <div>
                                    <p className="text-sm font-semibold text-foreground">Confetti Effects</p>
                                    <p className="text-[10px] text-foreground/40">Celebrate habit completions</p>
                                </div>
                            </div>
                            <button
                                type="button"
                                onClick={() => setConfettiEnabled(!confettiEnabled)}
                                className={`w-10 h-5 rounded-full transition-colors relative ${confettiEnabled ? 'bg-accent' : 'bg-foreground/10'}`}
                            >
                                <div className={`absolute top-1 w-3 h-3 rounded-full bg-white transition-all ${confettiEnabled ? 'right-1' : 'left-1'}`} />
                            </button>
                        </div>

                        <div className="flex items-center justify-between p-3 rounded-xl bg-foreground/[0.03] border border-foreground/[0.05]">
                            <div className="flex items-center gap-3">
                                <Volume2 className="w-4 h-4 text-primary" />
                                <div>
                                    <p className="text-sm font-semibold text-foreground">Sound Effects</p>
                                    <p className="text-[10px] text-foreground/40">Tactile audio feedback</p>
                                </div>
                            </div>
                            <button
                                type="button"
                                onClick={() => setSoundEnabled(!soundEnabled)}
                                className={`w-10 h-5 rounded-full transition-colors relative ${soundEnabled ? 'bg-primary' : 'bg-foreground/10'}`}
                            >
                                <div className={`absolute top-1 w-3 h-3 rounded-full bg-white transition-all ${soundEnabled ? 'right-1' : 'left-1'}`} />
                            </button>
                        </div>
                    </div>
                </div>

                {/* Reminders Section */}
                <div className="space-y-4">
                    <h3 className="text-[10px] uppercase tracking-widest font-bold text-muted-foreground flex items-center gap-2">
                        <Clock className="w-3 h-3" /> Daily Reminders
                    </h3>
                    <div className="relative group">
                        <input
                            type="time"
                            value={reminderTime}
                            onChange={(e) => setReminderTime(e.target.value)}
                            className="w-full bg-foreground/[0.03] border border-foreground/[0.05] rounded-xl px-4 py-2 text-sm text-foreground focus:outline-none focus:ring-1 focus:ring-accent transition-all group-hover:border-foreground/[0.1]"
                        />
                    </div>
                </div>

                <div className="pt-4">
                    <button
                        type="submit"
                        disabled={saving || !hasChanges}
                        className="btn-premium w-full flex items-center justify-center gap-2 py-2.5 rounded-xl disabled:opacity-50 disabled:cursor-not-allowed group relative overflow-hidden shadow-lg shadow-primary/20"
                    >
                        {saving ? (
                            <div className="w-4 h-4 border-2 border-white/20 border-t-white rounded-full animate-spin" />
                        ) : (
                            <>
                                <Save className="w-4 h-4 group-hover:scale-110 transition-transform" />
                                <span className="font-semibold">Save All Changes</span>
                            </>
                        )}
                    </button>
                </div>
            </form>

            <div className="mt-6 pt-4 border-t border-white/5">
                <AnimatePresence mode="wait">
                    {!showDeleteConfirm ? (
                        <motion.button
                            key="delete-btn"
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            exit={{ opacity: 0 }}
                            type="button"
                            onClick={() => setShowDeleteConfirm(true)}
                            className="w-full flex items-center justify-center gap-2 text-xs font-bold uppercase tracking-widest text-red-400/60 hover:text-red-400 transition-colors py-2"
                        >
                            <Trash2 className="w-3.5 h-3.5" />
                            Delete Account
                        </motion.button>
                    ) : (
                        <motion.div
                            key="delete-confirm"
                            initial={{ opacity: 0, y: 10 }}
                            animate={{ opacity: 1, y: 0 }}
                            exit={{ opacity: 0, y: 10 }}
                            className="bg-red-500/10 border border-red-500/20 rounded-xl p-3 space-y-3"
                        >
                            <div className="flex items-start gap-3">
                                <AlertTriangle className="w-5 h-5 text-red-400 mt-0.5" />
                                <div>
                                    <p className="text-sm font-bold text-red-400">Are you absolutely sure?</p>
                                    <p className="text-xs text-red-400/70 mt-1">
                                        This will permanently delete your profile, and all your habit streaks. This action cannot be undone.
                                    </p>
                                </div>
                            </div>
                            <div className="flex gap-2">
                                <button
                                    onClick={handleDeleteAccount}
                                    disabled={deleting}
                                    className="flex-1 bg-red-500 hover:bg-red-600 text-white rounded-xl py-2 text-xs font-bold transition-colors disabled:opacity-50"
                                >
                                    {deleting ? 'Deleting...' : 'Confirm Delete'}
                                </button>
                                <button
                                    onClick={() => setShowDeleteConfirm(false)}
                                    className="flex-1 bg-foreground/[0.05] hover:bg-foreground/[0.1] text-foreground rounded-xl py-2 text-xs font-bold transition-colors"
                                >
                                    Cancel
                                </button>
                            </div>
                        </motion.div>
                    )}
                </AnimatePresence>
            </div>
        </motion.div>
    );
}

'use client';

import { motion } from 'framer-motion';
import { Flame, TrendingUp, CheckCircle2, BarChart2 } from 'lucide-react';
import { DashboardStats } from '@/lib/types';
import { getStreakEmoji, formatPercent } from '@/lib/utils';
import { StatCardSkeleton } from '@/components/ui/skeleton';

interface StatsCardsProps {
    stats: DashboardStats | null;
    loading: boolean;
}

interface StatCardProps {
    label: string;
    value: string;
    sub?: string;
    icon: React.ElementType;
    gradient: string;
    accent: string;
    index: number;
}

function StatCard({
    label,
    value,
    sub,
    icon: Icon,
    gradient,
    accent,
    index,
}: StatCardProps) {
    return (
        <motion.div
            initial={{ opacity: 0, y: 15 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.05, duration: 0.5, ease: [0.16, 1, 0.3, 1] }}
            className="glass glass-hover rounded-xl p-4 relative overflow-hidden group shadow-sm hover:shadow-card transition-shadow"
        >
            {/* Top accent line */}
            <div
                className="absolute top-0 left-0 right-0 h-0.5 opacity-40 dark:opacity-40 opacity-20"
                style={{ background: accent }}
            />

            <div className="flex items-start justify-between mb-3">
                <div>
                    <p className="text-[10px] font-bold uppercase tracking-wider text-muted-foreground mb-1">
                        {label}
                    </p>
                    <div className="flex items-baseline gap-1">
                        <h3 className="text-2xl font-display font-bold leading-tight tracking-tight text-foreground">
                            {value}
                        </h3>
                    </div>
                </div>
                <div
                    className="w-9 h-9 rounded-lg flex items-center justify-center transition-transform group-hover:scale-110 duration-500 shadow-lg"
                    style={{ background: gradient }}
                    aria-hidden="true"
                >
                    <Icon className="w-4 h-4 text-white" />
                </div>
            </div>

            {sub && (
                <div className="flex items-center gap-1.5 mt-auto">
                    <span className="text-xs font-medium text-muted-foreground">
                        {sub}
                    </span>
                </div>
            )}

            {/* Background interactive gradient blur */}
            <div
                className="absolute -bottom-4 -right-4 w-20 h-20 blur-3xl rounded-full opacity-0 group-hover:opacity-10 dark:group-hover:opacity-20 transition-opacity duration-700 pointer-events-none"
                style={{ background: accent }}
            />
        </motion.div>
    );
}

export default function StatsCards({ stats, loading }: StatsCardsProps) {
    if (loading) {
        return (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
                {[1, 2, 3, 4].map((i) => <StatCardSkeleton key={i} />)}
            </div>
        );
    }

    if (!stats) return null;

    const cards = [
        {
            label: 'Current Streak',
            value: `${stats.currentStreak}d`,
            sub: `Best: ${stats.longestStreak} days`,
            icon: Flame,
            gradient: 'linear-gradient(135deg, #f97316, #ef4444)',
            accent: '#f97316',
        },
        {
            label: 'Completion Rate',
            value: formatPercent(stats.overallCompletionRate),
            sub: 'L30 Activity',
            icon: TrendingUp,
            gradient: 'linear-gradient(135deg, var(--primary), #5848d8)',
            accent: 'var(--primary)',
        },
        {
            label: 'Total Habits',
            value: String(stats.totalHabits),
            sub: 'Daily Actions',
            icon: CheckCircle2,
            gradient: 'linear-gradient(135deg, var(--accent), #00b388)',
            accent: 'var(--accent)',
        },
        {
            label: 'Consistency',
            value: `${stats.longestStreak}d`,
            sub: stats.longestStreak >= 7 ? 'Elite Level' : 'Developing',
            icon: BarChart2,
            gradient: 'linear-gradient(135deg, #8b5cf6, #6c63ff)',
            accent: '#8b5cf6',
        },
    ];

    return (
        <div
            className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-6"
            aria-label="Dashboard statistics"
        >
            {cards.map((card, i) => (
                <StatCard key={card.label} {...card} index={i} />
            ))}
        </div>
    );
}

'use client';

import { motion, AnimatePresence } from 'framer-motion';
import { X, Trophy, TrendingUp, Calendar, Zap, Share2 } from 'lucide-react';
import useSWR from 'swr';
import { statsApi } from '@/lib/api';
import { CheckSquare as CheckIcon, RefreshCw } from 'lucide-react';
import { toast } from 'sonner';

interface WeeklyReviewModalProps {
    isOpen: boolean;
    onClose: () => void;
}

export default function WeeklyReviewModal({ isOpen, onClose }: WeeklyReviewModalProps) {
    const { data: weeklyReview, isLoading } = useSWR(
        isOpen ? 'weekly-review' : null,
        () => statsApi.weeklyReview()
    );

    const handleShare = () => {
        toast.info('Sharing to social media... (Feature coming soon!)');
    };

    return (
        <AnimatePresence>
            {isOpen && (
                <>
                    {/* Backdrop */}
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        onClick={onClose}
                        className="fixed inset-0 bg-black/60 backdrop-blur-md z-[100]"
                    />

                    {/* Modal */}
                    <motion.div
                        initial={{ opacity: 0, scale: 0.9, y: 30 }}
                        animate={{ opacity: 1, scale: 1, y: 0 }}
                        exit={{ opacity: 0, scale: 0.9, y: 30 }}
                        className="fixed left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 w-full max-w-lg z-[101] px-4"
                    >
                        <div className="glass glass-hover rounded-[2rem] p-10 shadow-2xl border border-foreground/[0.05] text-center relative overflow-hidden">
                            {/* Background glows */}
                            <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-primary via-accent to-primary opacity-50" />
                            <div className="absolute -top-20 -right-20 w-64 h-64 bg-primary/10 blur-[100px] rounded-full" />
                            <div className="absolute -bottom-20 -left-20 w-64 h-64 bg-accent/10 blur-[100px] rounded-full" />

                            <button onClick={onClose} className="absolute top-6 right-6 p-2 hover:bg-foreground/[0.05] rounded-full transition-colors text-foreground/40 hover:text-foreground">
                                <X className="w-5 h-5" />
                            </button>

                            {isLoading ? (
                                <div className="space-y-6">
                                    <div className="flex flex-col items-center gap-2">
                                        <div className="h-8 w-48 skeleton" />
                                        <div className="h-4 w-64 skeleton" />
                                    </div>
                                    <div className="grid grid-cols-2 gap-4">
                                        {[1, 2, 3, 4].map(i => <div key={i} className="h-24 rounded-2xl skeleton" />)}
                                    </div>
                                    <div className="h-20 rounded-2xl skeleton" />
                                </div>
                            ) : weeklyReview?.hasHabits === false ? (
                                <div className="py-10 flex flex-col items-center">
                                    <div className="text-5xl mb-6 text-foreground/20">Γùê</div>
                                    <h2 className="text-xl font-bold text-foreground mb-2">No habits yet</h2>
                                    <p className="text-sm text-foreground/50 mb-8 max-w-[280px] mx-auto">
                                        Start tracking habits this week to see your review next Monday
                                    </p>
                                    <button
                                        onClick={onClose}
                                        className="w-full py-4 rounded-2xl bg-primary text-white font-bold hover:bg-primary/90 transition-all active:scale-95 shadow-lg shadow-primary/20"
                                    >
                                        Add My First Habit ΓåÆ
                                    </button>
                                </div>
                            ) : (
                                <>
                                    <motion.div
                                        initial={{ scale: 0.8, opacity: 0 }}
                                        animate={{ scale: 1, opacity: 1 }}
                                        transition={{ delay: 0.2, type: "spring" }}
                                        className="inline-flex items-center justify-center w-20 h-20 rounded-3xl bg-gradient-to-br from-primary to-accent shadow-lg shadow-primary/20 mb-8"
                                    >
                                        <Trophy className="w-10 h-10 text-white" />
                                    </motion.div>

                                    <h2 className="text-3xl font-display font-bold text-foreground mb-2">Weekly Momentum!</h2>
                                    <p className="text-muted-foreground mb-8">{weeklyReview?.weekRange.start} ΓÇô {weeklyReview?.weekRange.end}</p>

                                    <div className="grid grid-cols-2 gap-4 mb-10">
                                        {[
                                            {
                                                label: 'Completions',
                                                value: weeklyReview?.totalCompletions,
                                                icon: CheckIcon,
                                                color: weeklyReview?.totalCompletions === 0 ? 'text-red-500' : 'text-emerald-500'
                                            },
                                            {
                                                label: 'Success Rate',
                                                value: `${weeklyReview?.completionRate}%`,
                                                icon: TrendingUp,
                                                color: weeklyReview?.totalCompletions === 0 || (weeklyReview?.completionRate && weeklyReview.completionRate < 30) ? 'text-red-500' : 'text-primary'
                                            },
                                            {
                                                label: 'Best Habit',
                                                value: weeklyReview?.bestHabit?.title || 'None',
                                                icon: Trophy,
                                                color: 'text-amber-500'
                                            },
                                            {
                                                label: 'Best Streak',
                                                value: `${weeklyReview?.bestHabit?.streak || 0}d`,
                                                icon: Zap,
                                                color: 'text-rose-500'
                                            }
                                        ].map((item, idx) => (
                                            <div key={idx} className="bg-foreground/[0.03] border border-foreground/[0.05] rounded-2xl p-4 sm:p-5 flex flex-col items-center justify-center min-w-0">
                                                <span className="text-[10px] uppercase font-bold tracking-widest text-muted-foreground mb-2 text-center">{item.label}</span>
                                                <span className={`text-xl md:text-2xl font-display font-bold truncate max-w-full px-1 ${item.color}`}>
                                                    {item.label === 'Best Habit' && weeklyReview?.totalCompletions === 0 ? 'No completions' : item.value}
                                                </span>
                                            </div>
                                        ))}
                                    </div>

                                    <div className="bg-primary/[0.03] border border-primary/10 rounded-2xl p-6 mb-10">
                                        <p className="text-xs font-bold text-primary uppercase tracking-widest mb-3">Perfect This Week</p>
                                        <div className="flex flex-wrap justify-center gap-2">
                                            {weeklyReview?.perfectHabits.length ? weeklyReview.perfectHabits.map(h => (
                                                <div key={h.id} className="px-3 py-1.5 rounded-full text-[10px] font-bold bg-foreground/[0.04] border border-foreground/[0.08] text-foreground flex items-center gap-1.5">
                                                    <div className="w-1.5 h-1.5 rounded-full shadow-sm" style={{ backgroundColor: h.color }} />
                                                    {h.title}
                                                </div>
                                            )) : (
                                                <p className="text-sm text-muted-foreground italic">No perfect habits last week.</p>
                                            )}
                                        </div>
                                    </div>

                                    {weeklyReview?.needsAttentionHabits.length ? (
                                        <div className="bg-red-500/[0.03] border border-red-500/10 rounded-2xl p-6 mb-10">
                                            <p className="text-xs font-bold text-red-500 uppercase tracking-widest mb-3">Needs Attention</p>
                                            <div className="flex flex-wrap justify-center gap-2">
                                                {weeklyReview.needsAttentionHabits.map(h => (
                                                    <div key={h.id} className="px-3 py-1.5 rounded-full text-[10px] font-bold bg-foreground/[0.04] border border-red-500/20 text-foreground">
                                                        {h.title} <span className="text-red-500 ml-1">({h.missedDays} missed)</span>
                                                    </div>
                                                ))}
                                            </div>
                                        </div>
                                    ) : null}

                                    <div className="flex flex-col sm:flex-row gap-4">
                                        <button
                                            onClick={handleShare}
                                            className="flex-1 py-4 rounded-2xl bg-foreground text-background font-bold flex items-center justify-center gap-2 hover:opacity-90 transition-all active:scale-95 disabled:opacity-50"
                                            disabled={weeklyReview?.totalCompletions === 0}
                                        >
                                            <Share2 className="w-5 h-5" />
                                            Share Progress
                                        </button>
                                        <button
                                            onClick={onClose}
                                            className="flex-1 py-4 rounded-2xl bg-foreground/[0.05] hover:bg-foreground/[0.1] text-foreground font-bold transition-colors border border-foreground/[0.05]"
                                        >
                                            Keep Going ΓåÆ
                                        </button>
                                    </div>
                                </>
                            )}
                        </div>
                    </motion.div>
                </>
            )}
        </AnimatePresence>
    );
}

// Simple internal icon component if Lucide's CheckSquare isn't available
function CheckSquare({ className }: { className?: string }) {
    return <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}><path strokeLinecap="round" strokeLinejoin="round" d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-6 9l2 2 4-4" /></svg>;
}

'use client';

import { useState, useEffect, useCallback } from 'react';
import {
    DndContext,
    closestCenter,
    KeyboardSensor,
    PointerSensor,
    useSensor,
    useSensors,
    DragEndEvent,
    DragStartEvent,
    DragOverlay,
} from '@dnd-kit/core';
import {
    SortableContext,
    sortableKeyboardCoordinates,
    verticalListSortingStrategy,
    useSortable,
    arrayMove,
} from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { Habit } from '@/lib/types';
import HabitCard from './habit-card';
import { motion, AnimatePresence } from 'framer-motion';
import { habitsApi } from '@/lib/api';
import { toast } from 'sonner';

interface SortableHabitProps {
    habit: Habit;
    activeId: string | null;
    onEdit: (habit: Habit) => void;
    onDelete: (id: string) => void;
    onArchive: (id: string) => void;
    onComplete: (habitId: string, newStreak: number) => void;
}

function SortableHabit({ habit, activeId, onEdit, onDelete, onArchive, onComplete }: SortableHabitProps) {
    const { attributes, listeners, setNodeRef, transform, transition, isDragging } = useSortable({
        id: habit.id,
    });

    const style = {
        transform: CSS.Transform.toString(transform),
        transition,
        opacity: isDragging ? 0 : activeId ? 0.9 : 1, // Hide original if dragging, dim if other is dragging
    };

    return (
        <div ref={setNodeRef} style={style}>
            <HabitCard
                habit={habit}
                onEdit={onEdit}
                onDelete={onDelete}
                onArchive={onArchive}
                onComplete={onComplete}
                dragHandleProps={{ ...attributes, ...listeners }}
                isDragging={isDragging}
            />
        </div>
    );
}

interface DragDropListProps {
    habits: Habit[];
    onEdit: (habit: Habit) => void;
    onDelete: (id: string) => void;
    onArchive: (id: string) => void;
    onComplete: (habitId: string, newStreak: number) => void;
    onChange: (habits: Habit[]) => void;
}

export default function DragDropList({ habits, onEdit, onDelete, onArchive, onComplete, onChange }: DragDropListProps) {
    const [activeId, setActiveId] = useState<string | null>(null);
    const [localHabits, setLocalHabits] = useState(habits);

    useEffect(() => {
        setLocalHabits(habits);
    }, [habits]);

    const sensors = useSensors(
        useSensor(PointerSensor, { activationConstraint: { distance: 8 } }),
        useSensor(KeyboardSensor, { coordinateGetter: sortableKeyboardCoordinates })
    );

    const handleDragStart = (event: DragStartEvent) => {
        setActiveId(event.active.id as string);
    };

    const handleDragEnd = useCallback(
        async (event: DragEndEvent) => {
            setActiveId(null);
            const { active, over } = event;
            if (!over || active.id === over.id) return;

            const oldIndex = localHabits.findIndex((h) => h.id === active.id);
            const newIndex = localHabits.findIndex((h) => h.id === over.id);
            const reordered = arrayMove(localHabits, oldIndex, newIndex).map((h, i) => ({ ...h, order: i }));

            setLocalHabits(reordered);
            onChange(reordered);

            try {
                await habitsApi.reorder(reordered.map((h) => ({ id: h.id, order: h.order })));
            } catch {
                toast.error('Failed to save order. Please try again.');
                setLocalHabits(habits); // revert
                onChange(habits);
            }
        },
        [localHabits, habits, onChange]
    );

    const activeHabit = localHabits.find((h) => h.id === activeId);

    const containerVariants = {
        hidden: { opacity: 0 },
        visible: {
            opacity: 1,
            transition: {
                staggerChildren: 0.05,
            },
        },
    };

    const itemVariants = {
        hidden: { opacity: 0, y: 12 },
        visible: { opacity: 1, y: 0, transition: { duration: 0.3, ease: 'easeOut' as const } },
    };

    return (
        <DndContext
            sensors={sensors}
            collisionDetection={closestCenter}
            onDragStart={handleDragStart}
            onDragEnd={handleDragEnd}
            accessibility={{
                announcements: {
                    onDragStart: ({ active }) => {
                        const habit = localHabits.find((h) => h.id === active.id);
                        return `Picked up habit ${habit?.title}. It is in position ${localHabits.indexOf(habit!) + 1} of ${localHabits.length}.`;
                    },
                    onDragOver: ({ active, over }) => {
                        if (over) {
                            const activeHabit = localHabits.find((h) => h.id === active.id);
                            const overHabit = localHabits.find((h) => h.id === over.id);
                            return `${activeHabit?.title} is now over ${overHabit?.title}.`;
                        }
                        return '';
                    },
                    onDragEnd: ({ active }) => {
                        const habit = localHabits.find((h) => h.id === active.id);
                        return `${habit?.title} dropped.`;
                    },
                    onDragCancel: ({ active }) => {
                        const habit = localHabits.find((h) => h.id === active.id);
                        return `Drag cancelled. ${habit?.title} moved back to original position.`;
                    },
                },
            }}
        >
            <SortableContext items={localHabits.map((h) => h.id)} strategy={verticalListSortingStrategy}>
                <motion.div
                    variants={containerVariants}
                    initial="hidden"
                    animate="visible"
                    role="list"
                    aria-label="Habits list ΓÇô drag to reorder"
                    className="space-y-3"
                >
                    {localHabits.map((habit) => (
                        <motion.div key={habit.id} variants={itemVariants} role="listitem">
                            <SortableHabit
                                habit={habit}
                                activeId={activeId}
                                onEdit={onEdit}
                                onDelete={onDelete}
                                onArchive={onArchive}
                                onComplete={onComplete}
                            />
                        </motion.div>
                    ))}
                </motion.div>
            </SortableContext>

            <DragOverlay>
                {activeHabit && (
                    <HabitCard
                        habit={activeHabit}
                        onEdit={() => { }}
                        onDelete={() => { }}
                        onComplete={() => { }}
                        isDragging
                    />
                )}
            </DragOverlay>
        </DndContext>
    );
}

'use client';

import { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Check, Edit2, Trash2, GripVertical, Flame, ChevronRight, Archive, ArchiveRestore, Snowflake } from 'lucide-react';
import Link from 'next/link';
import { Habit } from '@/lib/types';
import { cn, getCategoryInfo, getCategoryClass, getStreakEmoji, isCompletedToday, getTodayString } from '@/lib/utils';
import { completionsApi, habitsApi } from '@/lib/api';
import { useSWRConfig } from 'swr';
import { toast } from 'sonner';
import { useAuth, ApiError } from '@/contexts/auth-context';
import { useCompletionFeedback } from '@/hooks/useCompletionFeedback';

interface HabitCardProps {
    habit: Habit;
    onEdit: (habit: Habit) => void;
    onDelete: (id: string) => void;
    onArchive?: (id: string) => void;
    onComplete: (habitId: string, newStreak: number) => void;
    dragHandleProps?: Record<string, unknown>;
    isDragging?: boolean;
}

export default function HabitCard({
    habit,
    onEdit,
    onDelete,
    onArchive,
    onComplete,
    dragHandleProps,
    isDragging,
}: HabitCardProps) {
    const { user } = useAuth();
    const { mutate: globalMutate } = useSWRConfig();
    const { triggerFeedback } = useCompletionFeedback();
    const [completing, setCompleting] = useState(false);
    const [localCompletions, setLocalCompletions] = useState(habit.completions || []);
    const [localStreak, setLocalStreak] = useState(habit.currentStreak || 0);

    const categoryInfo = getCategoryInfo(habit.category);
    const completedToday = isCompletedToday(localCompletions);
    const isFrozen = !!(habit.isFrozen && habit.frozenUntil && new Date(habit.frozenUntil) > new Date());
    const today = getTodayString();

    const handleFreeze = async (days: number) => {
        try {
            await habitsApi.freeze(habit.id, days);
            toast.success(`Habit frozen for ${days} days! Γ¥ä∩╕Å`);
            globalMutate(['habits', false]);
            globalMutate('dashboard-stats');
        } catch {
            toast.error('Failed to freeze habit.');
        }
    };

    const handleUnfreeze = async () => {
        try {
            await habitsApi.unfreeze(habit.id);
            toast.success('Habit unfrozen!');
            globalMutate(['habits', false]);
            globalMutate('dashboard-stats');
        } catch {
            toast.error('Failed to unfreeze habit.');
        }
    };

    const handleComplete = async (e: React.MouseEvent) => {
        e.preventDefault();
        e.stopPropagation();
        if (completing || completedToday || isFrozen) return;

        setCompleting(true);
        try {
            const res = await completionsApi.complete(habit.id);
            // Optimistic update
            setLocalCompletions([...localCompletions, { date: today } as typeof localCompletions[0]]);
            setLocalStreak(res.currentStreak);
            onComplete(habit.id, res.currentStreak);
            globalMutate('dashboard-stats');

            // Trigger confetti and sound feedback
            triggerFeedback(e);

            toast.success(`Γ£à ${habit.title} completed! Streak: ${res.currentStreak} ≡ƒöÑ`);
        } catch (err) {
            if (err instanceof ApiError && err.statusCode === 409) {
                toast.info("You've already completed this today!");
            } else {
                toast.error('Failed to mark as complete. Try again.');
            }
        } finally {
            setCompleting(false);
        }
    };

    const handleArchive = async (e: React.MouseEvent) => {
        e.preventDefault();
        e.stopPropagation();
        try {
            await habitsApi.archive(habit.id);
            toast.success(habit.archived ? 'Habit restored!' : 'Habit archived!');
            if (onArchive) onArchive(habit.id);
            globalMutate('dashboard-stats');
        } catch {
            toast.error('Failed to change archive status.');
        }
    };

    const handleDelete = (e: React.MouseEvent) => {
        e.preventDefault();
        e.stopPropagation();
        if (confirm(`Delete "${habit.title}"? This cannot be undone.`)) {
            onDelete(habit.id);
        }
    };

    const handleEdit = (e: React.MouseEvent) => {
        e.preventDefault();
        e.stopPropagation();
        onEdit(habit);
    };

    return (
        <motion.article
            layout
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.96 }}
            whileHover={{ y: isDragging ? 0 : -6, scale: isDragging ? 1.03 : 1.02 }}
            transition={{ type: 'spring', stiffness: 400, damping: 17, bounce: 0, ease: [0.16, 1, 0.3, 1] }}
            className={cn(
                'glass glass-hover rounded-xl p-3 mb-3 group relative',
                isDragging && 'shadow-[0_0_20px_rgba(99,102,241,0.4)] ring-2 ring-primary/40 rotate-1 z-50 cursor-grabbing bg-foreground/[0.05]',
                !isDragging && 'cursor-default transition-all',
                isFrozen && 'bg-blue-500/[0.05] border-blue-500/20 shadow-[0_0_15px_rgba(59,130,246,0.1)]'
            )}
            style={{
                border: isFrozen ? '1px solid rgba(59,130,246,0.2)' : completedToday ? '1px solid rgba(0,229,186,0.2)' : '1px solid var(--card-border)',
                background: isFrozen ? 'rgba(59, 130, 246, 0.03)' : completedToday ? 'rgba(0, 229, 186, 0.03)' : 'var(--card)',
            }}
            aria-label={`${habit.title}${completedToday ? ', completed today' : ''}`}
        >
            {isFrozen && (
                <div className="absolute top-3 right-3 flex items-center gap-1.5 px-2 py-0.5 rounded-full bg-blue-500/20 border border-blue-500/30 text-[10px] font-bold text-blue-500 dark:text-blue-400 z-10 animate-in fade-in zoom-in duration-300">
                    <Snowflake className="w-3 h-3" />
                    FROZEN
                </div>
            )}

            {/* Top accent line if completed */}
            {completedToday && !isFrozen && (
                <motion.div
                    initial={{ scaleX: 0 }}
                    animate={{ scaleX: 1 }}
                    className="absolute top-0 left-0 right-0 h-0.5 bg-accent origin-left rounded-t-2xl"
                />
            )}

            <div className="flex items-start gap-4">
                {/* Checkbox / Completion Indicator */}
                <div className="flex flex-col items-center gap-2 flex-shrink-0">
                    <button
                        onClick={handleComplete}
                        disabled={completing || completedToday || isFrozen}
                        className={cn(
                            "w-10 h-10 rounded-xl flex items-center justify-center transition-all duration-500 relative overflow-hidden group/btn",
                            completedToday
                                ? "bg-accent text-white shadow-[0_0_12px_var(--accent-glow)]"
                                : isFrozen
                                    ? "bg-blue-500/30 text-blue-500 dark:text-blue-400 border border-blue-500/40 cursor-not-allowed"
                                    : "bg-foreground/[0.05] hover:bg-foreground/[0.1] text-foreground/40 hover:text-foreground"
                        )}
                        aria-label={completedToday ? 'Completed' : 'Mark as complete'}
                    >
                        <AnimatePresence mode="wait">
                            {completing ? (
                                <motion.div
                                    key="loading"
                                    initial={{ opacity: 0 }}
                                    animate={{ opacity: 1 }}
                                    exit={{ opacity: 0 }}
                                    className="w-4 h-4 border-2 border-current border-t-transparent rounded-full animate-spin"
                                />
                            ) : completedToday ? (
                                <motion.div
                                    key="check"
                                    initial={{ scale: 0.5, opacity: 0 }}
                                    animate={{ scale: 1, opacity: 1 }}
                                    transition={{ type: "spring", stiffness: 400, damping: 10 }}
                                >
                                    <Check className="w-5 h-5 stroke-[3]" />
                                </motion.div>
                            ) : isFrozen ? (
                                <Snowflake className="w-5 h-5 animate-pulse" />
                            ) : (
                                <motion.div
                                    key="idle"
                                    initial={{ scale: 0.8 }}
                                    whileHover={{ scale: 1.1 }}
                                    className="group-hover/btn:scale-105 transition-transform"
                                >
                                    <Check className="w-5 h-5" />
                                </motion.div>
                            )}
                        </AnimatePresence>
                    </button>

                    {/* Drag handle */}
                    <div
                        {...dragHandleProps}
                        className="p-1.5 cursor-grab active:cursor-grabbing opacity-20 hover:opacity-100 transition-opacity rounded-md hover:bg-foreground/[0.05]"
                        aria-label="Drag to reorder"
                        role="button"
                        tabIndex={0}
                    >
                        <GripVertical className="w-3.5 h-3.5" />
                    </div>
                </div>

                {/* Content */}
                <div className="flex-1 min-w-0 pt-1">
                    <div className="flex items-center gap-2 mb-1.5">
                        <span className={cn('cat-badge', getCategoryClass(habit.category))}>
                            {categoryInfo.emoji} {categoryInfo.label}
                        </span>
                    </div>

                    <h3 className={cn(
                        "text-base font-display font-bold truncate transition-all duration-300",
                        (completedToday || isFrozen) ? "text-foreground/40 line-through decoration-foreground/20" : "text-foreground"
                    )}>
                        {habit.title}
                    </h3>

                    {habit.description && (
                        <p className="text-[11px] truncate mt-0.5 text-foreground/60 font-medium">
                            {habit.description}
                        </p>
                    )}

                    <div className="flex items-center gap-3 mt-2">
                        <div className="flex items-center gap-1 px-1.5 py-0.5 rounded-md bg-foreground/[0.03] border border-foreground/[0.05]">
                            <Flame className={cn("w-3 h-3 transition-colors", localStreak > 0 ? "text-orange-500" : "text-foreground/20")} />
                            <span className="text-[9px] font-bold uppercase tracking-wider text-foreground/70">
                                {localStreak} Day Streak
                            </span>
                            {localStreak > 0 && <span className="text-[11px]">{getStreakEmoji(localStreak)}</span>}
                        </div>
                    </div>
                </div>

                {/* Actions */}
                <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                    <div className="relative group/freeze">
                        {isFrozen ? (
                            <button
                                onClick={(e) => { e.preventDefault(); e.stopPropagation(); handleUnfreeze(); }}
                                className="p-2 rounded-xl bg-blue-500/20 text-blue-500 dark:text-blue-400 hover:bg-blue-500/30 transition-colors"
                                title="Unfreeze early"
                                aria-label="Unfreeze early"
                            >
                                <Snowflake className="w-4 h-4 animate-pulse" />
                            </button>
                        ) : (
                            <div className="p-2 rounded-xl hover:bg-foreground/[0.05] text-foreground/40 hover:text-foreground transition-colors cursor-default">
                                <Snowflake className="w-4 h-4" />
                                <div className="absolute bottom-full right-0 mb-2 opacity-0 group-hover/freeze:opacity-100 transition-opacity pointer-events-none">
                                    <div className="glass p-2 rounded-xl border border-foreground/[0.05] shadow-2xl flex flex-col gap-1 min-w-[100px]">
                                        <p className="text-[10px] uppercase font-bold text-muted-foreground mb-1 px-2">{habit.freezesAvailable} left</p>
                                        {[1, 2, 3].map(d => (
                                            <button
                                                key={d}
                                                onClick={(e) => { e.stopPropagation(); e.preventDefault(); handleFreeze(d); }}
                                                className="text-[10px] font-bold py-1 px-3 rounded-lg hover:bg-primary/[0.1] text-left transition-colors pointer-events-auto text-foreground"
                                            >
                                                {d} Day{d > 1 ? 's' : ''}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                    <button
                        onClick={handleEdit}
                        className="p-2 rounded-xl hover:bg-foreground/[0.05] transition-colors text-foreground/40 hover:text-foreground"
                        aria-label="Edit"
                    >
                        <Edit2 className="w-4 h-4" />
                    </button>
                    <button
                        onClick={handleArchive}
                        className="p-2 rounded-xl hover:bg-foreground/[0.05] transition-colors text-foreground/40 hover:text-foreground"
                        title={habit.archived ? "Restore" : "Archive"}
                        aria-label={habit.archived ? "Restore" : "Archive"}
                    >
                        {habit.archived ? <ArchiveRestore className="w-4 h-4" /> : <Archive className="w-4 h-4" />}
                    </button>
                    <button
                        onClick={handleDelete}
                        className="p-2 rounded-xl hover:bg-red-500/10 transition-colors text-foreground/40 hover:text-red-500"
                        aria-label="Delete"
                    >
                        <Trash2 className="w-4 h-4" />
                    </button>
                    <Link
                        href={`/habits/${habit.id}`}
                        className="p-2 rounded-xl hover:bg-foreground/[0.05] transition-colors text-foreground/40 hover:text-foreground"
                        aria-label="View details"
                    >
                        <ChevronRight className="w-4 h-4" />
                    </Link>
                </div>
            </div>

            {/* Optional Note Expandable */}
            <AnimatePresence>
                {completedToday && !isDragging && !isFrozen && (
                    <motion.div
                        initial={{ height: 0, opacity: 0 }}
                        animate={{ height: 'auto', opacity: 1 }}
                        exit={{ height: 0, opacity: 0 }}
                        className="overflow-hidden"
                    >
                        <div className="pt-3 mt-3 border-t border-foreground/[0.05]">
                            <form
                                onSubmit={async (e) => {
                                    e.preventDefault();
                                    const form = e.currentTarget;
                                    const note = (form.elements.namedItem('note') as HTMLInputElement).value;

                                    try {
                                        await completionsApi.updateNote(habit.id, today, note);
                                        // Update local state to reflect the note save
                                        const noteIndex = localCompletions.findIndex(c => c.date === today);
                                        if (noteIndex !== -1) {
                                            const newCompletions = [...localCompletions];
                                            newCompletions[noteIndex] = { ...newCompletions[noteIndex], notes: note };
                                            setLocalCompletions(newCompletions);
                                        }
                                        globalMutate('dashboard-stats');
                                        toast.success('Reflection saved!');
                                    } catch {
                                        toast.error('Failed to save reflection.');
                                    }
                                }}
                                className="flex gap-2"
                            >
                                <input
                                    name="note"
                                    placeholder="Add a reflection note..."
                                    className="flex-1 text-xs bg-foreground/[0.03] text-foreground border border-foreground/[0.05] rounded-lg px-3 py-1.5 focus:outline-none focus:ring-1 focus:ring-primary transition-all placeholder:text-muted-foreground"
                                    defaultValue={localCompletions.find(c => c.date === today)?.notes || ''}
                                />
                                <button
                                    type="submit"
                                    className="text-[10px] uppercase tracking-wider font-bold px-3 py-1.5 rounded-lg bg-foreground/[0.05] hover:bg-foreground/[0.1] text-foreground transition-colors"
                                >
                                    Save
                                </button>
                            </form>
                        </div>
                    </motion.div>
                )}
            </AnimatePresence>
        </motion.article>
    );
}

