'use client';

import { motion, AnimatePresence } from 'framer-motion';
import { X, Trophy, TrendingUp, Zap, Share2 } from 'lucide-react';
import useSWR from 'swr';
import { statsApi } from '@/lib/api';
import { CheckSquare as CheckIcon } from 'lucide-react';
import { toast } from 'sonner';

interface WeeklyReviewModalProps {
    isOpen: boolean;
    onClose: () => void;
}

export default function WeeklyReviewModal({ isOpen, onClose }: WeeklyReviewModalProps) {
    const { data: weeklyReview, isLoading } = useSWR(
        isOpen ? 'weekly-review' : null,
        () => statsApi.weeklyReview()
    );

    const handleShare = () => {
        toast.info('Sharing to social media... (Feature coming soon!)');
    };

    return (
        <AnimatePresence>
            {isOpen && (
                <>
                    {/* Backdrop */}
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        onClick={onClose}
                        className="fixed inset-0 bg-black/60 backdrop-blur-md z-[100]"
                    />

                    {/* Modal */}
                    <motion.div
                        initial={{ opacity: 0, scale: 0.9, y: 30 }}
                        animate={{ opacity: 1, scale: 1, y: 0 }}
                        exit={{ opacity: 0, scale: 0.9, y: 30 }}
                        className="fixed left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 w-full max-w-lg z-[101] px-4"
                    >
                        <div className="glass glass-hover rounded-[2rem] p-10 shadow-2xl border border-foreground/[0.05] text-center relative overflow-hidden">
                            {/* Background glows */}
                            <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-primary via-accent to-primary opacity-50" />
                            <div className="absolute -top-20 -right-20 w-64 h-64 bg-primary/10 blur-[100px] rounded-full" />
                            <div className="absolute -bottom-20 -left-20 w-64 h-64 bg-accent/10 blur-[100px] rounded-full" />

                            <button onClick={onClose} className="absolute top-6 right-6 p-2 hover:bg-foreground/[0.05] rounded-full transition-colors text-foreground/40 hover:text-foreground">
                                <X className="w-5 h-5" />
                            </button>

                            {isLoading ? (
                                <div className="space-y-6">
                                    <div className="flex flex-col items-center gap-2">
                                        <div className="h-8 w-48 skeleton" />
                                        <div className="h-4 w-64 skeleton" />
                                    </div>
                                    <div className="grid grid-cols-2 gap-4">
                                        {[1, 2, 3, 4].map(i => <div key={i} className="h-24 rounded-2xl skeleton" />)}
                                    </div>
                                    <div className="h-20 rounded-2xl skeleton" />
                                </div>
                            ) : weeklyReview?.hasHabits === false ? (
                                <div className="py-10 flex flex-col items-center">
                                    <div className="text-5xl mb-6 text-foreground/20">â—ˆ</div>
                                    <h2 className="text-xl font-bold text-foreground mb-2">No habits yet</h2>
                                    <p className="text-sm text-foreground/50 mb-8 max-w-[280px] mx-auto">
                                        Start tracking habits this week to see your review next Monday
                                    </p>
                                    <button
                                        onClick={onClose}
                                        className="w-full py-4 rounded-2xl bg-primary text-white font-bold hover:bg-primary/90 transition-all active:scale-95 shadow-lg shadow-primary/20"
                                    >
                                        Add My First Habit â†’
                                    </button>
                                </div>
                            ) : (
                                <>
                                    <motion.div
                                        initial={{ scale: 0.8, opacity: 0 }}
                                        animate={{ scale: 1, opacity: 1 }}
                                        transition={{ delay: 0.2, type: "spring" }}
                                        className="inline-flex items-center justify-center w-20 h-20 rounded-3xl bg-gradient-to-br from-primary to-accent shadow-lg shadow-primary/20 mb-8"
                                    >
                                        <Trophy className="w-10 h-10 text-white" />
                                    </motion.div>

                                    <h2 className="text-3xl font-display font-bold text-foreground mb-2">Weekly Momentum!</h2>
                                    <p className="text-muted-foreground mb-8">{weeklyReview?.weekRange.start} â€“ {weeklyReview?.weekRange.end}</p>

                                    <div className="grid grid-cols-2 gap-4 mb-10">
                                        {[
                                            {
                                                label: 'Completions',
                                                value: weeklyReview?.totalCompletions,
                                                icon: CheckIcon,
                                                color: weeklyReview?.totalCompletions === 0 ? 'text-red-500' : 'text-emerald-500'
                                            },
                                            {
                                                label: 'Success Rate',
                                                value: `${weeklyReview?.completionRate}%`,
                                                icon: TrendingUp,
                                                color: weeklyReview?.totalCompletions === 0 || (weeklyReview?.completionRate && weeklyReview.completionRate < 30) ? 'text-red-500' : 'text-primary'
                                            },
                                            {
                                                label: 'Best Habit',
                                                value: weeklyReview?.bestHabit?.title || 'None',
                                                icon: Trophy,
                                                color: 'text-amber-500'
                                            },
                                            {
                                                label: 'Best Streak',
                                                value: `${weeklyReview?.bestHabit?.streak || 0}d`,
                                                icon: Zap,
                                                color: 'text-rose-500'
                                            }
                                        ].map((item, idx) => (
                                            <div key={idx} className="bg-foreground/[0.03] border border-foreground/[0.05] rounded-2xl p-4 sm:p-5 flex flex-col items-center justify-center min-w-0">
                                                <span className="text-[10px] uppercase font-bold tracking-widest text-muted-foreground mb-2 text-center">{item.label}</span>
                                                <span className={`text-xl md:text-2xl font-display font-bold truncate max-w-full px-1 ${item.color}`}>
                                                    {item.label === 'Best Habit' && weeklyReview?.totalCompletions === 0 ? 'No completions' : item.value}
                                                </span>
                                            </div>
                                        ))}
                                    </div>

                                    <div className="bg-primary/[0.03] border border-primary/10 rounded-2xl p-6 mb-10">
                                        <p className="text-xs font-bold text-primary uppercase tracking-widest mb-3">Perfect This Week</p>
                                        <div className="flex flex-wrap justify-center gap-2">
                                            {weeklyReview?.perfectHabits.length ? weeklyReview.perfectHabits.map(h => (
                                                <div key={h.id} className="px-3 py-1.5 rounded-full text-[10px] font-bold bg-foreground/[0.04] border border-foreground/[0.08] text-foreground flex items-center gap-1.5">
                                                    <div className="w-1.5 h-1.5 rounded-full shadow-sm" style={{ backgroundColor: h.color }} />
                                                    {h.title}
                                                </div>
                                            )) : (
                                                <p className="text-sm text-muted-foreground italic">No perfect habits last week.</p>
                                            )}
                                        </div>
                                    </div>

                                    {weeklyReview?.needsAttentionHabits.length ? (
                                        <div className="bg-red-500/[0.03] border border-red-500/10 rounded-2xl p-6 mb-10">
                                            <p className="text-xs font-bold text-red-500 uppercase tracking-widest mb-3">Needs Attention</p>
                                            <div className="flex flex-wrap justify-center gap-2">
                                                {weeklyReview.needsAttentionHabits.map(h => (
                                                    <div key={h.id} className="px-3 py-1.5 rounded-full text-[10px] font-bold bg-foreground/[0.04] border border-red-500/20 text-foreground">
                                                        {h.title} <span className="text-red-500 ml-1">({h.missedDays} missed)</span>
                                                    </div>
                                                ))}
                                            </div>
                                        </div>
                                    ) : null}

                                    <div className="flex flex-col sm:flex-row gap-4">
                                        <button
                                            onClick={handleShare}
                                            className="flex-1 py-4 rounded-2xl bg-foreground text-background font-bold flex items-center justify-center gap-2 hover:opacity-90 transition-all active:scale-95 disabled:opacity-50"
                                            disabled={weeklyReview?.totalCompletions === 0}
                                        >
                                            <Share2 className="w-5 h-5" />
                                            Share Progress
                                        </button>
                                        <button
                                            onClick={onClose}
                                            className="flex-1 py-4 rounded-2xl bg-foreground/[0.05] hover:bg-foreground/[0.1] text-foreground font-bold transition-colors border border-foreground/[0.05]"
                                        >
                                            Keep Going â†’
                                        </button>
                                    </div>
                                </>
                            )}
                        </div>
                    </motion.div>
                </>
            )}
        </AnimatePresence>
    );
}

// Simple internal icon component if Lucide's CheckSquare isn't available
function CheckSquare({ className }: { className?: string }) {
    return <svg className={className} fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}><path strokeLinecap="round" strokeLinejoin="round" d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-6 9l2 2 4-4" /></svg>;
}

'use client';

import { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Check, Edit2, Trash2, GripVertical, Flame, ChevronRight, Archive, ArchiveRestore, Snowflake } from 'lucide-react';
import Link from 'next/link';
import { Habit } from '@/lib/types';
import { cn, getCategoryInfo, getCategoryClass, getStreakEmoji, isCompletedToday, getTodayString } from '@/lib/utils';
import { completionsApi, habitsApi } from '@/lib/api';
import { useSWRConfig } from 'swr';
import { toast } from 'sonner';
import { useAuth, ApiError } from '@/contexts/auth-context';
import { useCompletionFeedback } from '@/hooks/useCompletionFeedback';

interface HabitCardProps {
    habit: Habit;
    onEdit: (habit: Habit) => void;
    onDelete: (id: string) => void;
    onArchive?: (id: string) => void;
    onComplete: (habitId: string, newStreak: number) => void;
    dragHandleProps?: Record<string, unknown>;
    isDragging?: boolean;
}

export default function HabitCard({
    habit,
    onEdit,
    onDelete,
    onArchive,
    onComplete,
    dragHandleProps,
    isDragging,
}: HabitCardProps) {
    const { mutate: globalMutate } = useSWRConfig();
    const { triggerFeedback } = useCompletionFeedback();
    const [completing, setCompleting] = useState(false);
    const [localCompletions, setLocalCompletions] = useState(habit.completions || []);
    const [localStreak, setLocalStreak] = useState(habit.currentStreak || 0);

    const categoryInfo = getCategoryInfo(habit.category);
    const completedToday = isCompletedToday(localCompletions);
    const isFrozen = !!(habit.isFrozen && habit.frozenUntil && new Date(habit.frozenUntil) > new Date());
    const today = getTodayString();

    const handleFreeze = async (days: number) => {
        try {
            await habitsApi.freeze(habit.id, days);
            toast.success(`Habit frozen for ${days} days! â„ï¸`);
            globalMutate(['habits', false]);
            globalMutate('dashboard-stats');
        } catch {
            toast.error('Failed to freeze habit.');
        }
    };

    const handleUnfreeze = async () => {
        try {
            await habitsApi.unfreeze(habit.id);
            toast.success('Habit unfrozen!');
            globalMutate(['habits', false]);
            globalMutate('dashboard-stats');
        } catch {
            toast.error('Failed to unfreeze habit.');
        }
    };

    const handleComplete = async (e: React.MouseEvent) => {
        e.preventDefault();
        e.stopPropagation();
        if (completing || completedToday || isFrozen) return;

        setCompleting(true);
        try {
            const res = await completionsApi.complete(habit.id);
            // Optimistic update
            setLocalCompletions([...localCompletions, { date: today } as typeof localCompletions[0]]);
            setLocalStreak(res.currentStreak);
            onComplete(habit.id, res.currentStreak);
            globalMutate('dashboard-stats');

            // Trigger confetti and sound feedback
            triggerFeedback(e);

            toast.success(`âœ… ${habit.title} completed! Streak: ${res.currentStreak} ðŸ”¥`);
        } catch (err) {
            if (err instanceof ApiError && err.statusCode === 409) {
                toast.info("You've already completed this today!");
            } else {
                toast.error('Failed to mark as complete. Try again.');
            }
        } finally {
            setCompleting(false);
        }
    };

    const handleArchive = async (e: React.MouseEvent) => {
        e.preventDefault();
        e.stopPropagation();
        try {
            await habitsApi.archive(habit.id);
            toast.success(habit.archived ? 'Habit restored!' : 'Habit archived!');
            if (onArchive) onArchive(habit.id);
            globalMutate('dashboard-stats');
        } catch {
            toast.error('Failed to change archive status.');
        }
    };

    const handleDelete = (e: React.MouseEvent) => {
        e.preventDefault();
        e.stopPropagation();
        if (confirm(`Delete "${habit.title}"? This cannot be undone.`)) {
            onDelete(habit.id);
        }
    };

    const handleEdit = (e: React.MouseEvent) => {
        e.preventDefault();
        e.stopPropagation();
        onEdit(habit);
    };

    return (
        <motion.article
            layout
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.96 }}
            whileHover={{ y: isDragging ? 0 : -6, scale: isDragging ? 1.03 : 1.02 }}
            transition={{ type: 'spring', stiffness: 400, damping: 17, bounce: 0, ease: [0.16, 1, 0.3, 1] }}
            className={cn(
                'glass glass-hover rounded-xl p-3 mb-3 group relative',
                isDragging && 'shadow-[0_0_20px_rgba(99,102,241,0.4)] ring-2 ring-primary/40 rotate-1 z-50 cursor-grabbing bg-foreground/[0.05]',
                !isDragging && 'cursor-default transition-all',
                isFrozen && 'bg-blue-500/[0.05] border-blue-500/20 shadow-[0_0_15px_rgba(59,130,246,0.1)]'
            )}
            style={{
                border: isFrozen ? '1px solid rgba(59,130,246,0.2)' : completedToday ? '1px solid rgba(0,229,186,0.2)' : '1px solid var(--card-border)',
                background: isFrozen ? 'rgba(59, 130, 246, 0.03)' : completedToday ? 'rgba(0, 229, 186, 0.03)' : 'var(--card)',
            }}
            aria-label={`${habit.title}${completedToday ? ', completed today' : ''}`}
        >
            {isFrozen && (
                <div className="absolute top-3 right-3 flex items-center gap-1.5 px-2 py-0.5 rounded-full bg-blue-500/20 border border-blue-500/30 text-[10px] font-bold text-blue-500 dark:text-blue-400 z-10 animate-in fade-in zoom-in duration-300">
                    <Snowflake className="w-3 h-3" />
                    FROZEN
                </div>
            )}

            {/* Top accent line if completed */}
            {completedToday && !isFrozen && (
                <motion.div
                    initial={{ scaleX: 0 }}
                    animate={{ scaleX: 1 }}
                    className="absolute top-0 left-0 right-0 h-0.5 bg-accent origin-left rounded-t-2xl"
                />
            )}

            <div className="flex items-start gap-4">
                {/* Checkbox / Completion Indicator */}
                <div className="flex flex-col items-center gap-2 flex-shrink-0">
                    <button
                        onClick={handleComplete}
                        disabled={completing || completedToday || isFrozen}
                        className={cn(
                            "w-10 h-10 rounded-xl flex items-center justify-center transition-all duration-500 relative overflow-hidden group/btn",
                            completedToday
                                ? "bg-accent text-white shadow-[0_0_12px_var(--accent-glow)]"
                                : isFrozen
                                    ? "bg-blue-500/30 text-blue-500 dark:text-blue-400 border border-blue-500/40 cursor-not-allowed"
                                    : "bg-foreground/[0.05] hover:bg-foreground/[0.1] text-foreground/40 hover:text-foreground"
                        )}
                        aria-label={completedToday ? 'Completed' : 'Mark as complete'}
                    >
                        <AnimatePresence mode="wait">
                            {completing ? (
                                <motion.div
                                    key="loading"
                                    initial={{ opacity: 0 }}
                                    animate={{ opacity: 1 }}
                                    exit={{ opacity: 0 }}
                                    className="w-4 h-4 border-2 border-current border-t-transparent rounded-full animate-spin"
                                />
                            ) : completedToday ? (
                                <motion.div
                                    key="check"
                                    initial={{ scale: 0.5, opacity: 0 }}
                                    animate={{ scale: 1, opacity: 1 }}
                                    transition={{ type: "spring", stiffness: 400, damping: 10 }}
                                >
                                    <Check className="w-5 h-5 stroke-[3]" />
                                </motion.div>
                            ) : isFrozen ? (
                                <Snowflake className="w-5 h-5 animate-pulse" />
                            ) : (
                                <motion.div
                                    key="idle"
                                    initial={{ scale: 0.8 }}
                                    whileHover={{ scale: 1.1 }}
                                    className="group-hover/btn:scale-105 transition-transform"
                                >
                                    <Check className="w-5 h-5" />
                                </motion.div>
                            )}
                        </AnimatePresence>
                    </button>

                    {/* Drag handle */}
                    <div
                        {...dragHandleProps}
                        className="p-1.5 cursor-grab active:cursor-grabbing opacity-20 hover:opacity-100 transition-opacity rounded-md hover:bg-foreground/[0.05]"
                        aria-label="Drag to reorder"
                        role="button"
                        tabIndex={0}
                    >
                        <GripVertical className="w-3.5 h-3.5" />
                    </div>
                </div>

                {/* Content */}
                <div className="flex-1 min-w-0 pt-1">
                    <div className="flex items-center gap-2 mb-1.5">
                        <span className={cn('cat-badge', getCategoryClass(habit.category))}>
                            {categoryInfo.emoji} {categoryInfo.label}
                        </span>
                    </div>

                    <h3 className={cn(
                        "text-base font-display font-bold truncate transition-all duration-300",
                        (completedToday || isFrozen) ? "text-foreground/40 line-through decoration-foreground/20" : "text-foreground"
                    )}>
                        {habit.title}
                    </h3>

                    {habit.description && (
                        <p className="text-[11px] truncate mt-0.5 text-foreground/60 font-medium">
                            {habit.description}
                        </p>
                    )}

                    <div className="flex items-center gap-3 mt-2">
                        <div className="flex items-center gap-1 px-1.5 py-0.5 rounded-md bg-foreground/[0.03] border border-foreground/[0.05]">
                            <Flame className={cn("w-3 h-3 transition-colors", localStreak > 0 ? "text-orange-500" : "text-foreground/20")} />
                            <span className="text-[9px] font-bold uppercase tracking-wider text-foreground/70">
                                {localStreak} Day Streak
                            </span>
                            {localStreak > 0 && <span className="text-[11px]">{getStreakEmoji(localStreak)}</span>}
                        </div>
                    </div>
                </div>

                {/* Actions */}
                <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                    <div className="relative group/freeze">
                        {isFrozen ? (
                            <button
                                onClick={(e) => { e.preventDefault(); e.stopPropagation(); handleUnfreeze(); }}
                                className="p-2 rounded-xl bg-blue-500/20 text-blue-500 dark:text-blue-400 hover:bg-blue-500/30 transition-colors"
                                title="Unfreeze early"
                                aria-label="Unfreeze early"
                            >
                                <Snowflake className="w-4 h-4 animate-pulse" />
                            </button>
                        ) : (
                            <div className="p-2 rounded-xl hover:bg-foreground/[0.05] text-foreground/40 hover:text-foreground transition-colors cursor-default">
                                <Snowflake className="w-4 h-4" />
                                <div className="absolute bottom-full right-0 mb-2 opacity-0 group-hover/freeze:opacity-100 transition-opacity pointer-events-none">
                                    <div className="glass p-2 rounded-xl border border-foreground/[0.05] shadow-2xl flex flex-col gap-1 min-w-[100px]">
                                        <p className="text-[10px] uppercase font-bold text-muted-foreground mb-1 px-2">{habit.freezesAvailable} left</p>
                                        {[1, 2, 3].map(d => (
                                            <button
                                                key={d}
                                                onClick={(e) => { e.stopPropagation(); e.preventDefault(); handleFreeze(d); }}
                                                className="text-[10px] font-bold py-1 px-3 rounded-lg hover:bg-primary/[0.1] text-left transition-colors pointer-events-auto text-foreground"
                                            >
                                                {d} Day{d > 1 ? 's' : ''}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                    <button
                        onClick={handleEdit}
                        className="p-2 rounded-xl hover:bg-foreground/[0.05] transition-colors text-foreground/40 hover:text-foreground"
                        aria-label="Edit"
                    >
                        <Edit2 className="w-4 h-4" />
                    </button>
                    <button
                        onClick={handleArchive}
                        className="p-2 rounded-xl hover:bg-foreground/[0.05] transition-colors text-foreground/40 hover:text-foreground"
                        title={habit.archived ? "Restore" : "Archive"}
                        aria-label={habit.archived ? "Restore" : "Archive"}
                    >
                        {habit.archived ? <ArchiveRestore className="w-4 h-4" /> : <Archive className="w-4 h-4" />}
                    </button>
                    <button
                        onClick={handleDelete}
                        className="p-2 rounded-xl hover:bg-red-500/10 transition-colors text-foreground/40 hover:text-red-500"
                        aria-label="Delete"
                    >
                        <Trash2 className="w-4 h-4" />
                    </button>
                    <Link
                        href={`/habits/${habit.id}`}
                        className="p-2 rounded-xl hover:bg-foreground/[0.05] transition-colors text-foreground/40 hover:text-foreground"
                        aria-label="View details"
                    >
                        <ChevronRight className="w-4 h-4" />
                    </Link>
                </div>
            </div>

            {/* Optional Note Expandable */}
            <AnimatePresence>
                {completedToday && !isDragging && !isFrozen && (
                    <motion.div
                        initial={{ height: 0, opacity: 0 }}
                        animate={{ height: 'auto', opacity: 1 }}
                        exit={{ height: 0, opacity: 0 }}
                        className="overflow-hidden"
                    >
                        <div className="pt-3 mt-3 border-t border-foreground/[0.05]">
                            <form
                                onSubmit={async (e) => {
                                    e.preventDefault();
                                    const form = e.currentTarget;
                                    const note = (form.elements.namedItem('note') as HTMLInputElement).value;

                                    try {
                                        await completionsApi.updateNote(habit.id, today, note);
                                        // Update local state to reflect the note save
                                        const noteIndex = localCompletions.findIndex(c => c.date === today);
                                        if (noteIndex !== -1) {
                                            const newCompletions = [...localCompletions];
                                            newCompletions[noteIndex] = { ...newCompletions[noteIndex], notes: note };
                                            setLocalCompletions(newCompletions);
                                        }
                                        globalMutate('dashboard-stats');
                                        toast.success('Reflection saved!');
                                    } catch {
                                        toast.error('Failed to save reflection.');
                                    }
                                }}
                                className="flex gap-2"
                            >
                                <input
                                    name="note"
                                    placeholder="Add a reflection note..."
                                    className="flex-1 text-xs bg-foreground/[0.03] text-foreground border border-foreground/[0.05] rounded-lg px-3 py-1.5 focus:outline-none focus:ring-1 focus:ring-primary transition-all placeholder:text-muted-foreground"
                                    defaultValue={localCompletions.find(c => c.date === today)?.notes || ''}
                                />
                                <button
                                    type="submit"
                                    className="text-[10px] uppercase tracking-wider font-bold px-3 py-1.5 rounded-lg bg-foreground/[0.05] hover:bg-foreground/[0.1] text-foreground transition-colors"
                                >
                                    Save
                                </button>
                            </form>
                        </div>
                    </motion.div>
                )}
            </AnimatePresence>
        </motion.article>
    );
}

